import EmailAndPassword from "./EmailAndPassword";
import ForgotPasswordLink from "./ForgotPasswordLink";
import LoginButton from "./LoginButton";
import RememberLogin from "./RememberLogin";
import SignUpButton from "./SignUpButton";
import { Container, Row } from "react-bootstrap";
import React, { useState, useEffect } from "react";
import sqlite from "react-native-sqlite-storage";

const db = sqlite.openDatabase(
  {
    name: "test.db",
    location: "default",
  },
  () => {},
  (error) => {
    console.log(error);
  }
);

const LoginForm = () => {
  // Step 1: State Management
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState("");

  useEffect(() => {
    // Check if Users table exists in the database
    db.transaction((tx) => {
      tx.executeSql(
        "SELECT name FROM sqlite_master WHERE type='table' AND name='Users';",
        [],
        (_, result) => {
          if (result.rows.length === 0) {
            console.log("Table 'Users' does not exist");
            setLoginError("Invalid credentials"); // For simplicity, assuming error for now
          }
        },
        (_, error) => {
          console.log("Error checking table existence:", error);
          setLoginError("Invalid credentials"); // For simplicity, assuming error for now
        }
      );
    });
  }, []);

  // Step 2: Form Submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the email and password match an entry in the database
    db.transaction((tx) => {
      tx.executeSql(
        "SELECT * FROM Users WHERE email=? AND password=?;",
        [email, password],
        (_, result) => {
          if (result.rows.length > 0) {
            console.log("User login successful");
            // Add logic here for successful login
          } else {
            console.log("Invalid credentials");
            setLoginError("Invalid credentials");
          }
        },
        (_, error) => {
          console.log("Error checking credentials:", error);
          setLoginError("Invalid credentials");
        }
      );
    });
  };

  return (
    <Container fluid>
      <div className="w-25 p-5"></div>
      <Row>
        <div className="login_form">
          <div className="container flex flex-col mx-auto">
            <div className="flex flex-col mb-16 mx-auto">
              <h1 className="max-w-md text-4xl font-bold text-center">
                Sign in to Frag Pulse
              </h1>
            </div>
            <form onSubmit={handleSubmit} className="flex flex-col items-center">
              <EmailAndPassword
                email={email}
                setEmail={setEmail}
                password={password}
                setPassword={setPassword}
              />
              <div className="form-check max-w-sm mx-auto mb-5">
                <RememberLogin />
                <ForgotPasswordLink />
              </div>
              <div className="flex flex-col items-center w-full">
                <LoginButton />
              </div>
              <div className="max-w-sm mx-auto">
                <hr className="h-px my-8 bg-gray-200 border-0 dark:bg-gray-700" />
              </div>
              <SignUpButton />
              {loginError && (
                <div className="text-red-500 mt-2">{loginError}</div>
              )}
            </form>
          </div>
        </div>
      </Row>
    </Container>
  );
};

export default LoginForm;
