import { useState } from 'react';

import {Form, Button, Modal} from 'react-bootstrap'

const AccountFormProfile = ({showProfileSubmittion, handleShow, handleClose}) => {
    const [onSubmit, SetSubmit] = useState(false);
    const [profileImage, setProfileImage] = useState('');
    const [profileName, setProfileName] = useState('');
    const [profileBio, setProfileBio] = useState('');

    const handleSubmit = (event) => {
        const form = event.target;
        event.preventDefault();
        const profileUpdate = { profileImage, profileName, profileBio };
        SetSubmit(true);
        handleShow();
        console.log(profileUpdate)
    }

    return (
        <Form onSubmit={handleSubmit}>
        <h2>Edit Profile</h2>
        <Form.Group controlId="formFile" className="mb-3">
            <Form.Label>Profile Image</Form.Label>
            <Form.Control 
                type="file"
                name='ProfileImage'
                value={profileImage}

            />
        </Form.Group>

        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Label>Profile Name</Form.Label>
            <Form.Control 
                type="text" 
                name='ProfileName'
                placeholder="Profile Name" 
                value={profileName}


            />
        </Form.Group>

        <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
            <Form.Label>Bio Entry</Form.Label>
            <Form.Control 
                as="textarea" 
                rows={3}
                name='ProfileBio'
                value={profileBio} 

            />
        </Form.Group>

        <Button variant="primary" type="submit" onClick={handleSubmit}>
            Submit
        </Button>

        <Modal show={showProfileSubmittion} onHide={handleClose}>
            <Modal.Header closeButton>
            <Modal.Title>Profile Submittion</Modal.Title>
            </Modal.Header>
            <Modal.Body className="text-center">Success!</Modal.Body>
            <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
                Close
            </Button>
            </Modal.Footer>
        </Modal>

        </Form>
    )
}

export default AccountFormProfile
